import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import RelatedDoctors from "./RelatedDoctors";
import axios from "axios";

const AppointmentContainer = () => {
    const { id: docId } = useParams();
    const daysOfWeek = ['SUN', 'MON', 'TUE', 'WED', 'THU', 'FRI', 'SAT'];

    const [docInfo, setDocInfo] = useState(null);
    const [schedules, setSchedules] = useState({});
    const [slotIndex, setSlotIndex] = useState(0);
    const [slotTime, setSlotTime] = useState('');

    const fetchDocInfo = async () => {
        try {
            const response = await axios.get(`http://localhost:8080/api/doctors/${docId}`);
            setDocInfo(response.data);
        } catch (error) {
            console.error("Error fetching doctor info:", error);
        }
    };

    const fetchSchedules = async () => {
        try {
            const response = await axios.get(`http://localhost:8080/api/schedules/doctors/${docId}/available`);
            const data = response.data;

            const groupedByDate = {};
            data.forEach(slot => {
                if (!groupedByDate[slot.date]) {
                    groupedByDate[slot.date] = [];
                }
                groupedByDate[slot.date].push(slot);
            });

            // Sort keys (dates) in ascending order
            const sorted = Object.keys(groupedByDate).sort().reduce((obj, key) => {
                obj[key] = groupedByDate[key];
                return obj;
            }, {});

            setSchedules(sorted);
        } catch (error) {
            console.error("Error fetching doctor schedules:", error);
        }
    };

    useEffect(() => {
        fetchDocInfo();
    }, [docId]);

    useEffect(() => {
        if (docInfo) fetchSchedules();
    }, [docInfo]);

    const dateKeys = Object.keys(schedules);

    return docInfo && (
        <div>
            {/* Doctor Details */}
            <div className='flex flex-col sm:flex-row gap-4'>
                <div>
                    <img className='bg-primary w-full sm:max-w-72 rounded-lg' src={docInfo.imageUrl} alt={docInfo.name} />
                </div>
                <div className='doctorInfo'>
                    <p className='flex items-center gap-2 text-2xl font-medium text-gray-900'>
                        {docInfo.name}
                    </p>
                    <div className='flex items-center gap-2 text-sm mt-1 text-gray-600'>
                        <p>{docInfo.degree} - {docInfo.departmentName}</p>
                        <button className='py-0.5 px-2 border text-xs rounded-full'>{docInfo.experienceYears} years</button>
                    </div>
                    <div className='mt-3'>
                        <p className='text-sm text-gray-500'>Bác sĩ nhiều kinh nghiệm trong chuyên khoa {docInfo.departmentName}.</p>
                    </div>
                    <p className='text-gray-500 font-medium mt-2'>
                        Appointment fee: <span>50.000đ</span>
                    </p>
                </div>
            </div>

            {/* Booking Slots */}
            <div className='sm:ml-72 sm:pl-4 mt-4 font-medium text-gray-700'>
                <p>Booking slots</p>
                <div className='flex gap-3 items-center w-full overflow-x-scroll mt-4'>
                    {dateKeys.map((date, index) => {
                        const dateObj = new Date(date);
                        return (
                            <div
                                onClick={() => {
                                    setSlotIndex(index);
                                    setSlotTime('');
                                }}
                                className={`text-center py-6 min-w-16 rounded-full cursor-pointer ${slotIndex === index ? 'bg-primary text-white' : 'border border-gray-200'}`}
                                key={index}
                            >
                                <p>{daysOfWeek[dateObj.getDay()]}</p>
                                <p>{dateObj.getDate()}</p>
                            </div>
                        );
                    })}
                </div>
                <div className='flex items-center gap-3 w-full overflow-x-scroll mt-4'>
                    {dateKeys.length > 0 && schedules[dateKeys[slotIndex]]?.map((slot, index) => {
                        const timeRange = `${slot.startTime.slice(0, 5)} - ${slot.endTime.slice(0, 5)}`;
                        return (
                            <p
                                onClick={() => setSlotTime(timeRange)}
                                className={`text-sm font-light flex-shrink-0 px-5 py-2 rounded-full cursor-pointer ${slotTime === timeRange ? 'bg-primary text-white' : 'text-gray-400 border border-gray-300'}`}
                                key={index}
                            >
                                {timeRange}
                            </p>
                        );
                    })}
                </div>
                <button className='bg-primary text-white text-sm font-light px-14 py-3 rounded-full my-6'>
                    Book an appointment
                </button>
            </div>

            {/* Related Doctors */}
            {/*<RelatedDoctors docId={docId} speciality={docInfo.departmentName} />*/}
        </div>
    );
};

export default AppointmentContainer;
