import React from 'react';
import  { useEffect, useState } from 'react';

const Topbar = () => {
    // const [userData, setUserData] = useState(null);
    // useEffect(() => {
    //     // Lấy dữ liệu từ sessionStorage với key là 'user'
    //     const storedData = sessionStorage.getItem('user');
    //
    //     // Kiểm tra nếu dữ liệu tồn tại
    //     if (storedData) {
    //         // Parse dữ liệu nếu nó là JSON
    //         setUserData(JSON.parse(storedData));
    //     } else {
    //         console.log('Không tìm thấy dữ liệu trong sessionStorage');
    //     }
    // }, []);
    const userData = {
        fullname:"Det dep trai"
    }
    const [isDropdownOpen, setIsDropdownOpen] = useState(false);
    const dropdownStyle = {
        position: 'relative',
        zIndex: 1000,
    };
    const dropdownMenuStyle = {
        display: isDropdownOpen ? 'block' : 'none', // Hiển thị/ẩn khi hover
        position: 'absolute',
        zIndex: 3000, // Đảm bảo dropdown nằm trên các phần tử khác
        backgroundColor: '#fff', // Màu nền dropdown
        border: '1px solid #ddd', // Viền
        borderRadius: '0 0 0 0', // rounded-0 rounded-bottom
        margin: 0, // m-0
        minWidth: '160px', // Đảm bảo dropdown có chiều rộng tối thiểu
        boxShadow: '0 2px 5px rgba(0, 0, 0, 0.1)', // Hiệu ứng bóng
    };
    const dropdownItemStyle = {
        color: 'red',
        fontStyle: 'italic',
        padding: '8px 16px', // Khoảng cách bên trong
        cursor: 'pointer',
        transition: 'background-color 0.3s ease', // Hiệu ứng mượt mà khi hover
    };
    return (
        <>
            <div className="container-fluid bg-light p-0 wow fadeIn" data-wow-delay="0.1s">
                <div className="row gx-0 d-none d-lg-flex">
                    <div className="col-lg-7 px-5 text-start">
                        <div className="h-100 d-inline-flex align-items-center py-3 me-4">
                            <small className="fa fa-map-marker-alt text-primary me-2"></small>
                            <small>123 Street, New York, USA</small>
                        </div>
                        <div className="h-100 d-inline-flex align-items-center py-3">
                            <small className="far fa-clock text-primary me-2"></small>
                            <small>Mon - Fri : 09.00 AM - 09.00 PM</small>
                        </div>
                    </div>
                    <div className="col-lg-5 px-5 text-end">
                        {userData ? (
                            <div className="h-100 d-inline-flex align-items-center py-3 me-4"
                                 onMouseEnter={() => setIsDropdownOpen(true)} // Hiển thị dropdown khi hover
                                 onMouseLeave={() => setIsDropdownOpen(false)} // Ẩn dropdown khi rời chuột
                            >
                                <div className="nav-item dropdown">
                                    <a href="#" className="nav-link dropdown-toggle" style={{ position: 'relative', zIndex: 1000 }} data-bs-toggle="dropdown">{userData.fullname}</a>
                                    <div className="dropdown-menu rounded-0 rounded-bottom m-0" style={{zIndex:3000}}>
                                        <div className="dropdown-item" style={{color:"red", fontStyle:"italic"}}>Đăng xuất</div>
                                    </div>
                                </div>
                            </div>
                        ) : (
                            <div className="h-100 d-inline-flex align-items-center py-3 me-4">
                                <a href="/signin" style={{
                                    color: "qua",
                                    fontStyle: "italic",
                                    textDecoration: "underline",
                                    fontSize: "large"
                                }}>Sign In</a>
                            </div>
                        )}
                        {/*<div className="h-100 d-inline-flex align-items-center py-3 me-4">*/}
                        {/*    <small className="fa fa-phone-alt text-primary me-2"></small>*/}
                        {/*    <small>+012 345 6789</small>*/}
                        {/*</div>*/}
                        {/*<div className="h-100 d-inline-flex align-items-center">*/}
                        {/*    <a className="btn btn-sm-square rounded-circle bg-white text-primary me-1" href="#"><i className="fab fa-facebook-f"></i></a>*/}
                        {/*    <a className="btn btn-sm-square rounded-circle bg-white text-primary me-1" href="#"><i className="fab fa-twitter"></i></a>*/}
                        {/*    <a className="btn btn-sm-square rounded-circle bg-white text-primary me-1" href="#"><i className="fab fa-linkedin-in"></i></a>*/}
                        {/*    <a className="btn btn-sm-square rounded-circle bg-white text-primary me-0" href="#"><i className="fab fa-instagram"></i></a>*/}
                        {/*</div>*/}
                    </div>
                </div>
            </div>
        </>
    )
};

export default Topbar;
